<!DOCTYPE html><html><head>	<title>Gallery Foto</title>	<style>		Body{		background: black;		}			</style>	<script type="text/javascript" src="<?php echo base_url();?>js/jquery.js"></script>	<script type="text/javascript" src="<?php echo base_url();?>js/jquery-1.4.min.js"></script>	<script src="<?php echo base_url();?>js/jquery.cycle.all.min.js" type="text/javascript"></script>	<link type="text/css" href="<?php echo base_url();?>css/gallery.css" rel="stylesheet">	<script>	$(document).ready(function() {			var move = 0		    zoom = 1.1;		$('.item').hover(function() {			width = $('.item').width() * zoom;			height = $('.item').height() * zoom;					$(this).find('img').stop(false,true).animate({'width':width, 'height':height, 'top':move, 'left':move, 'padding': '10px'}, {duration:300});			$(this).find('div.caption').stop(false,true).fadeIn(300);		},				function() {			$(this).find('img').stop(false,true).animate({'width':$('.item').width(), 'height':$('.item').height(), 'top':'0', 'left':'0', 'padding': '10px'}, {duration:300});			$(this).find('div.caption').stop(false,true).fadeOut(300);		});			$('a[name=modal]').click(function(e) {		e.preventDefault();				var id = $(this).attr('href');		var maskHeight = $(document).height();		var maskWidth = $(window).width();			$('#mask').css({'width':maskWidth,'height':maskHeight});		$('#mask').fadeIn(1000);			$('#mask').fadeTo("slow",0.8);				var winH = $(window).height();		var winW = $(window).width();              		$(id).css('top',  winH/2-$(id).height()/2);		$(id).css('left', winW/2-$(id).width()/2);			$(id).fadeIn(2000); 	});		$('.window .close').click(function (e) {		e.preventDefault();		$('#mask').hide();		$('.window').hide();	});				$('#mask').click(function () {		$(this).hide();		$('.window').hide();	});			});			function Pilih(Gambar, indeks){		document.images.besar.src=Gambar;		document.form.gambar.options.selectedIndex=indeks;	}			</script></head>	<audio autoplay>	 <source src="http://localhost/galeri_foto/lagu/Bruno Mars - The Lazy Song.mp3" type="audio/mp3"/> 	</audio>	<h1 align="center">Photo Gallery</h1>		<div id="photo"><div class="gambar" align="center">		<?php 			$pertama='';			if (isset($images) && count($images)){				foreach($images as $image){					$pertama= $image['url'];					break;				}			echo ("<img src=".$pertama." name='besar' width='600' height='500'>"); 			}		?>				</div></div>				<div id="gallery" Title="List Image">		<?php if (isset($images) && count($images)):			$i=0;			foreach($images as $image){	?>			<div class="item">				<a href="<?php echo "JavaScript:Pilih('".$image['url']."',".$i.")"; ?>">					<img src="<?php echo $image['thumb_url']; ?>">				</a>				<div class="caption"><?php echo basename($image['url']); ?></div>							</div>		<?php $i=$i+1;}		  else: ?>			<div id="blank_gallery">Please Upload an Image</div>		<?php endif; ?>			</div>		<div id="mask"></div></body></html>